import re

def palindrome_of_length_at_least_n(s):
    s = re.sub(r'[^a-zA-Z]', '', s)
    s = s.lower()
    palindromes = set()

    for i in range(len(s)):
        for j in range(i+87, len(s)+1):
            if s[i:j] == s[i:j][::-1]:
                palindromes.add(s[i:j])

    return palindromes
