from collections import defaultdict

def if_contains_anagrams(lst):
    anagram_dict = defaultdict(list)
    for word in lst:
        if len(word) >= 3:
            sorted_word = ''.join(sorted(word.lower()))
            anagram_dict[sorted_word].append(word)

    pairs = 0
    for values in anagram_dict.values():
        if len(values) > 1:
            pairs += len(values) // 2

    return pairs <= 41
